mapper.select:
SELECT *
FROM FWK_APPLICATION
WHERE 1=1
AND APPLICATION_ID=#{param.applicationId}
AND application_name like concat("%", #{param.applicationName}, "%")
AND created_date = #{param.createdDate}
AND modified_date = #{param.modifiedDate}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM FWK_APPLICATION
WHERE 1=1
AND APPLICATION_ID=#{applicationId}
AND application_name like concat("%", #{applicationName}, "%")
AND created_date = #{createdDate}
AND modified_date = #{modifiedDate}

SELECT APPLICATION_ID
FROM FWK_APPLICATION
where application_name = #{applicationName}

mapper.update:
UPDATE FWK_APPLICATION
SET application_name=#{applicationName}, modified_date = sysdate()
WHERE APPLICATION_ID=#{applicationId}

UPDATE  FWK_APPLICATION
SET modified_date= sysdate()
WHERE APPLICATION_ID=#{applicationId}
